  async generateAISummary(messages) {
    let discussionText = '';
    
    try {
      // Generate and download the discussion text file
      discussionText = this.downloadDiscussionFile(messages);
      
      console.log('Generated discussion text:', discussionText.substring(0, 200) + '...');
      
      // Get AI summary from Gemini
      console.log('Calling Gemini API...');
      const summary = await this.callGeminiAPI(discussionText);
      
      console.log('AI Summary received:', summary.substring(0, 200) + '...');
      
      // Check if this is the fallback summary
      const isRealAI = !summary.includes('Generated Offline');
      
      return {
        success: isRealAI,
        summary: summary,
        textFileGenerated: true,
        timestamp: new Date().toISOString(),
        apiUsed: isRealAI
