import React, { useState, useRef, useEffect } from 'react';
import './Discussion.css';

const Discussion = () => {
  const [messages, setMessages] = useState([
    {
      id: 1,
      sender: 'Admin',
      message: '‡§∏‡•ç‡§µ‡§æ‡§ó‡§§ ‡§π‡•à ‡§∏‡§π‡§æ ‡§ó‡•ç‡§∞‡§æ‡§Æ ‡§ö‡§∞‡•ç‡§ö‡§æ ‡§Æ‡•á‡§Ç! ‡§Ø‡§π‡§æ‡§Å ‡§Ü‡§™ ‡§Ö‡§™‡§®‡•á ‡§µ‡§ø‡§ö‡§æ‡§∞ ‡§∏‡§æ‡§ù‡§æ ‡§ï‡§∞ ‡§∏‡§ï‡§§‡•á ‡§π‡•à‡§Ç‡•§',
      time: '10:00 AM',
      isAdmin: true,
      avatar: 'üèõÔ∏è'
    },
    {
      id: 2,
      sender: '‡§∞‡§æ‡§Æ ‡§∂‡§∞‡•ç‡§Æ‡§æ',
      message: '‡§®‡§Æ‡§∏‡•ç‡§§‡•á! ‡§ó‡§æ‡§Å‡§µ ‡§ï‡•Ä ‡§∏‡§°‡§º‡§ï‡•ã‡§Ç ‡§ï‡•Ä ‡§∏‡•ç‡§•‡§ø‡§§‡§ø ‡§ï‡•á ‡§¨‡§æ‡§∞‡•á ‡§Æ‡•á‡§Ç ‡§ö‡§∞‡•ç‡§ö‡§æ ‡§ï‡§∞‡§®‡§æ ‡§ö‡§æ‡§π‡§§‡§æ ‡§π‡•Ç‡§Å‡•§',
      time: '10:05 AM',
      isAdmin: false,
      avatar: 'üë®'
    },
    {
      id: 3,
      sender: '‡§∏‡•Ä‡§§‡§æ ‡§¶‡•á‡§µ‡•Ä',
      message: '‡§ú‡•Ä ‡§π‡§æ‡§Å, ‡§¨‡§æ‡§∞‡§ø‡§∂ ‡§ï‡•á ‡§¨‡§æ‡§¶ ‡§∏‡§°‡§º‡§ï‡•á‡§Ç ‡§¨‡§π‡•Å‡§§ ‡§ñ‡§∞‡§æ‡§¨ ‡§π‡•ã ‡§ó‡§à ‡§π‡•à‡§Ç‡•§ ‡§ú‡§≤‡•ç‡§¶‡•Ä ‡§Æ‡§∞‡§Æ‡•ç‡§Æ‡§§ ‡§ï‡•Ä ‡§ú‡§∞‡•Ç‡§∞‡§§ ‡§π‡•à‡•§',
      time: '10:07 AM',
      isAdmin: false,
      avatar: 'üë©'
    }
  ]);
  
  const [newMessage, setNewMessage] = useState('');
  const [isTyping, setIsTyping] = useState(false);
  const messagesEndRef = useRef(null);

  const scrollToBottom = () => {
    messagesEndRef.current?.scrollIntoView({ behavior: 'smooth' });
  };

  useEffect(() => {
    scrollToBottom();
  }, [messages]);

  const handleSendMessage = () => {
    if (newMessage.trim()) {
      const message = {
        id: messages.length + 1,
        sender: '‡§Ü‡§™',
        message: newMessage,
        time: new Date().toLocaleTimeString([], { hour: '2-digit', minute: '2-digit' }),
        isAdmin: false,
        avatar: 'üë§'
      };
      
      setMessages([...messages, message]);
      setNewMessage('');
      
      // Simulate admin response
      setTimeout(() => {
        setIsTyping(true);
        setTimeout(() => {
          setIsTyping(false);
          const adminResponse = {
            id: messages.length + 2,
            sender: 'Moderator',
            message: '‡§Ü‡§™‡§ï‡§æ ‡§∏‡§Ç‡§¶‡•á‡§∂ ‡§™‡•ç‡§∞‡§æ‡§™‡•ç‡§§ ‡§π‡•Å‡§Ü‡•§ ‡§π‡§Æ ‡§á‡§∏ ‡§Æ‡•Å‡§¶‡•ç‡§¶‡•á ‡§™‡§∞ ‡§ß‡•ç‡§Ø‡§æ‡§® ‡§¶‡•á‡§Ç‡§ó‡•á‡•§',
            time: new Date().toLocaleTimeString([], { hour: '2-digit', minute: '2-digit' }),
            isAdmin: true,
            avatar: 'üë®‚Äçüíº'
          };
          setMessages(prev => [...prev, adminResponse]);
        }, 2000);
      }, 1000);
    }
  };

  const handleKeyPress = (e) => {
    if (e.key === 'Enter' && !e.shiftKey) {
      e.preventDefault();
      handleSendMessage();
    }
  };

  return (
    <div className="discussion-container">
      {/* Header */}
      <div className="discussion-header">
        <div className="header-content">
          <div className="header-icon">üó£Ô∏è</div>
          <div className="header-info">
            <h2 className="header-title">‡§∏‡§æ‡§Æ‡•Å‡§¶‡§æ‡§Ø‡§ø‡§ï ‡§ö‡§∞‡•ç‡§ö‡§æ</h2>
            <p className="header-subtitle">Community Discussion</p>
          </div>
          <div className="online-indicator">
            <span className="online-dot"></span>
            <span className="online-text">12 ‡§∏‡§ï‡•ç‡§∞‡§ø‡§Ø</span>
          </div>
        </div>
        <div className="header-decoration"></div>
      </div>

      {/* Messages Area */}
      <div className="messages-container">
        {messages.map((msg) => (
          <div key={msg.id} className={`message ${msg.isAdmin ? 'admin-message' : 'user-message'}`}>
            <div className="message-avatar">{msg.avatar}</div>
            <div className="message-content">
              <div className="message-header">
                <span className="sender-name">{msg.sender}</span>
                <span className="message-time">{msg.time}</span>
              </div>
              <div className="message-text">{msg.message}</div>
            </div>
          </div>
        ))}
        
        {isTyping && (
          <div className="message admin-message typing-indicator">
            <div className="message-avatar">üë®‚Äçüíº</div>
            <div className="message-content">
              <div className="typing-animation">
                <span></span>
                <span></span>
                <span></span>
              </div>
            </div>
          </div>
        )}
        
        <div ref={messagesEndRef} />
      </div>

      {/* Input Area */}
      <div className="input-container">
        <div className="input-wrapper">
          <button className="attachment-btn" title="Attach File">
            üìé
          </button>
          <textarea
            value={newMessage}
            onChange={(e) => setNewMessage(e.target.value)}
            onKeyPress={handleKeyPress}
            placeholder="‡§Ö‡§™‡§®‡§æ ‡§∏‡§Ç‡§¶‡•á‡§∂ ‡§≤‡§ø‡§ñ‡•á‡§Ç..."
            className="message-input"
            rows="1"
          />
          <button 
            onClick={handleSendMessage}
            className="send-btn"
            disabled={!newMessage.trim()}
            title="Send Message"
          >
            ‚û§
          </button>
        </div>
        
        {/* Government Footer */}
        <div className="input-footer">
          <div className="gov-notice">
            <span className="notice-icon">‚ö†Ô∏è</span>
            <span className="notice-text">‡§ï‡•É‡§™‡§Ø‡§æ ‡§∏‡§≠‡•ç‡§Ø ‡§≠‡§æ‡§∑‡§æ ‡§ï‡§æ ‡§™‡•ç‡§∞‡§Ø‡•ã‡§ó ‡§ï‡§∞‡•á‡§Ç | Please use respectful language</span>
          </div>
        </div>
      </div>
    </div>
  );
};

export default Discussion;
